mapper.update:
update account_tbl
set money = #{money,jdbcType=DECIMAL},
  id_default = #{idDefault}
where id = #{id} and id_default = #{idDefault}

update account_tbl
set freeze_money = #{freezeMoney,jdbcType=DECIMAL}
where id = #{id}

update account_tbl
set freeze_money = #{freezeMoney,jdbcType=DECIMAL},
    money = #{money,jdbcType=DECIMAL}
where id = #{id}
